import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { IProductModel } from 'src/app/models/product-model';
import { ProductsService } from 'src/app/services/products.service';
import { TokenStorageService } from 'src/app/services/token-storage.service';

@Component({
  selector: 'app-products-page',
  templateUrl: './products-page.component.html',
  styleUrls: ['./products-page.component.css']
})
export class ProductsPageComponent implements OnInit {

  constructor(
    private tokenStorageService: TokenStorageService,
    private productsService: ProductsService
  ) { }

  products$: Observable<IProductModel[]>;
  adminCheck: boolean; 
  term: string = '';

  ngOnInit(): void {
    this.adminCheck = (this.tokenStorageService.getUserRole() == 'admin')? true: false;
    this.products$ = this.productsService.getAll();
  }

}
<div>
  <div style="width: 30%;" >
    <nz-input-group [nzSuffix]="suffixIconSearch">
      <input [(ngModel)]="term" type="text" nz-input placeholder="input search text" />
    </nz-input-group>
    <ng-template #suffixIconSearch>
      <span nz-icon nzType="search"></span>
    </ng-template>
  </div>

  <div *ngIf="adminCheck; else elseBlock">
    <app-products-admin [term]="term" [products$]="products$"></app-products-admin>
  </div>
  <ng-template #elseBlock>
    <app-products-user [term]="term" [products$]="products$"></app-products-user>
  </ng-template>
</div>
